mapper.select:
SELECT
-- 级联表的字段 ,kecheng.jiaoshi_id as kechengJiaoshiId ,kecheng.kecheng_name as kechengName ,kecheng.kecheng_uuid_number as kechengUuidNumber ,kecheng.kecheng_photo as kechengPhoto ,kecheng.kecheng_video as kechengVideo ,kecheng.kecheng_file as kechengFile ,kecheng.kecheng_types as kechengTypes ,kecheng.kecheng_clicknum as kechengClicknum ,kecheng.kecheng_content as kechengContent ,kecheng.zhishiyaodian_content as zhishiyaodianContent ,kecheng.kaoshidagang_content as kaoshidagangContent ,kecheng.shangxia_types as shangxiaTypes ,kecheng.kecheng_delete as kechengDelete ,xuesheng.xuesheng_name as xueshengName ,xuesheng.xuesheng_phone as xueshengPhone ,xuesheng.xuesheng_id_number as xueshengIdNumber ,xuesheng.xuesheng_photo as xueshengPhoto ,xuesheng.banji_types as banjiTypes ,xuesheng.xuesheng_email as xueshengEmail
FROM kecheng_collection a left JOIN kecheng kecheng ON a.kecheng_id = kecheng.id left JOIN xuesheng xuesheng ON a.xuesheng_id = xuesheng.id
and a.id in
#{item}
and ( a.kecheng_id = #{params.kechengId} )
and ( a.xuesheng_id = #{params.xueshengId} )
and a.kecheng_collection_types = #{params.kechengCollectionTypes}
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.kecheng_id IS NOT NULL
and kecheng.jiaoshi_id = #{params.jiaoshiId}
and kecheng.kecheng_name like CONCAT('%',#{params.kechengName},'%')
and kecheng.kecheng_uuid_number like CONCAT('%',#{params.kechengUuidNumber},'%')
and kecheng.kecheng_video like CONCAT('%',#{params.kechengVideo},'%')
and kecheng.kecheng_types = #{params.kechengTypes}
and kecheng.kecheng_clicknum >= #{params.kechengClicknumStart}
and kecheng.kecheng_clicknum <= #{params.kechengClicknumEnd}
and kecheng.kecheng_clicknum = #{params.kechengClicknum}
and kecheng.kecheng_content like CONCAT('%',#{params.kechengContent},'%')
and kecheng.zhishiyaodian_content like CONCAT('%',#{params.zhishiyaodianContent},'%')
and kecheng.kaoshidagang_content like CONCAT('%',#{params.kaoshidagangContent},'%')
and kecheng.shangxia_types = #{params.shangxiaTypes}
and kecheng.kecheng_delete >= #{params.kechengDeleteStart}
and kecheng.kecheng_delete <= #{params.kechengDeleteEnd}
and kecheng.kecheng_delete = #{params.kechengDelete}
and a.xuesheng_id IS NOT NULL
and xuesheng.xuesheng_name like CONCAT('%',#{params.xueshengName},'%')
and xuesheng.xuesheng_phone like CONCAT('%',#{params.xueshengPhone},'%')
and xuesheng.xuesheng_id_number like CONCAT('%',#{params.xueshengIdNumber},'%')
and xuesheng.banji_types = #{params.banjiTypes}
and xuesheng.xuesheng_email like CONCAT('%',#{params.xueshengEmail},'%')
order by a.${params.orderBy} desc
